sources.sass/layout/_problems.scss:
.problems{
    background-image: linear-gradient(to bottom,
        var(--main-black-color) 73.5%,
        var(--white-color) 26.5%);
    padding-top: 24px;
    padding-bottom: 40px;
}

.problems{
    counter-reset: item-counter;
}

.problems__list{
    background-color: white;
    box-shadow: -5px 8px 26px rgba(0, 0, 0, 0.04);
    padding: 20px 12px 24px 32px;
}

.problems__title{
    font-family: inherit;
    font-weight: 800;
    font-size: 24px;
    line-height: 1.3;
    letter-spacing: 0.03em;
    color: var(--white-color);
    margin-bottom: 24px;
}
.problems__title-span{
    color: var(--accent-color);
}

.problems__item{
    font-family: inherit;
    line-height: 1.43;
    color: var(--main-black-color);
    display: flex;
    flex-direction: column;
    @media (max-width: 479px) {
        &:not(:last-child){
        margin-bottom: 16px;
        }
    }

    counter-increment: item-counter;

    &::before {
        content: "— " counter(item-counter);
        color: var(--accent-color);
        margin-bottom: 5px;
    }
}

@media screen and (min-width:768px) and (max-width:1679px) {
    .problems{
        padding-top: 40px;
        background-image: linear-gradient(to bottom,
            var(--main-black-color) 66.8%,
            var(--white-color) 66.8%);
    }
    .problems__list{
        display: flex;
        flex-wrap: wrap;
        padding: 32px 80px;
    }
    .problems__item{
        width: calc((100% - 40px)/2);   

        &:not(:nth-child(2n)){
            margin-right: 40px;
        }
        &:not(:nth-last-child(-n + 2)){
            margin-bottom: 24px;
        }
        &::before{
            margin-bottom: 4px;
        }
    }
    .problems__title{
        font-size: 26px;
        margin-bottom: 25px;
    }

}

@media screen and (min-width:1680px){
    .problems {
        padding-top: 80px;
        padding-bottom: 80px;
        background-image: linear-gradient(to bottom,
            var(--main-black-color) 69.4%,
            var(--white-color) 69.4%);
    }
    .problems__container{
        background-image: url(../images/problems/flame.svg);
        background-repeat: no-repeat;
        background-size: 415px 442px;
        background-position: right top;
    }
    .problems__list {
        display: flex;
        flex-wrap: wrap;
        padding: 60px 192px 60px 140px;
    }

    .problems__item {
        font-size: 20px;
        line-height: 1.4;
        width: calc((100% - 308px)/3);
        margin-right: 85px;
        &:nth-child(3n-1){
            margin-right: 223px;
        }
        &:nth-child(3n){
            margin-right: 0;
        }
        &:not(:nth-last-child(-n + 3)) {
            margin-bottom: 40px;
        }
        &::before{
            margin-bottom: 8px;
        }
    }
    .problems__title {
        font-size: 43px;
        line-height: 1.3;
        margin-bottom: 80px;
    }
}

sources.sass/layout/_how-we-do.scss:
.doings {
