  letter-spacing: 0.03em;
  color: var(--main-black-color);
}

.how-do-we-do {
  display: block;
}

.how-do-we-do__item:not(:last-child) {
  padding-bottom: 16px;
}

.how-do-we-do__link {
  text-align: center;
  align-items: center;
}

.how-do-we-do__box {
  margin: auto;
  padding-top: 32px;
  width: 280px;
  height: 280px;
  background-color: var(--white-color);
  box-sizing: border-box;
  box-shadow: 0px 8px 26px rgba(0, 0, 0, 0.1);
