&::after {
    content: "";
    display: block;
    position: absolute;
    margin-top: -185px;
    right: -33px;
    height: 268px;
    width: 368px;
    background-image: url("../images/program/program_tablet.png");
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;

    @media (min-device-pixel-ratio: 2),
